#!/usr/bin/env bash
env=$1 # online or test
if [ "$env" == "test" ]; then
    # replace file for testApi
    file="src/client/requestInfo.js"
    cp ${file}.test $file
fi

FILEEXTERN='.zip'
VERSION='4.16.7'
FILENAME='aip-node-sdk-'$VERSION$FILEEXTERN

mkdir output
zip -r output/$FILENAME src/* test/* package.json README.md
zip -d output/$FILENAME src/client/requestInfo.js.test
